import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const resultRef = useRef(null);
  const [result, setResult] = useState(null);
  const [operator, setOperator] = useState("");

  function calculate(value) {
    if (!isNaN(value)) {
      switch (operator) {
        case "+":
          setResult((prevResult) =>
            prevResult !== null ? prevResult + value : value
          );
          break;
        case "-":
          setResult((prevResult) =>
            prevResult !== null ? prevResult - value : -value
          );
          break;
        case "x":
          setResult((prevResult) =>
            prevResult !== null ? prevResult * value : value
          );
          break;
        case "รท":
          setResult((prevResult) =>
            prevResult !== null ? prevResult / value : value
          );
          break;
        default:
          setResult(value);
      }
    }
  }

  function handleOperation(e) {
    e.preventDefault();
    setOperator(e.target.textContent);
  }

  function handleNumber(num) {
    calculate(num);
  }

  function handleEquals() {
    calculate(parseFloat(resultRef.current.textContent));
    setOperator("");
  }

  function resetResult() {
    setResult(null);
    setOperator("");
  }

  return (
    <div className="calculator">
      <div>
        <h1>Simplest Working Calculator</h1>
      </div>
      <div className="display" ref={resultRef}>
        {result !== null ? result : 0}
      </div>
      <div className="buttons">
        <button onClick={() => handleNumber(7)}>7</button>
        <button onClick={() => handleNumber(8)}>8</button>
        <button onClick={() => handleNumber(9)}>9</button>
        <button onClick={handleOperation} className="operator">
          +
        </button>
        <button onClick={() => handleNumber(4)}>4</button>
        <button onClick={() => handleNumber(5)}>5</button>
        <button onClick={() => handleNumber(6)}>6</button>
        <button onClick={handleOperation} className="operator">
          -
        </button>
        <button onClick={() => handleNumber(1)}>1</button>
        <button onClick={() => handleNumber(2)}>2</button>
        <button onClick={() => handleNumber(3)}>3</button>
        <button onClick={handleOperation} className="operator">
          x
        </button>
        <button onClick={resetResult}>C</button>
        <button onClick={() => handleNumber(0)}>0</button>
        <button onClick={handleEquals} className="equal">
          =
        </button>
        <button onClick={handleOperation} className="operator">
          รท
        </button>
      </div>
    </div>
  );
}

export default App;
