import React, { useState, useRef } from "react";
import "./App.css";

function App() {
  const inputRef = useRef(null);
  const resultRef = useRef(null);
  const [result, setResult] = useState(null);
  const [operator, setOperator] = useState("");

  function calculate() {
    const inputValue = Number(inputRef.current.value);
    if (!isNaN(inputValue)) {
      switch (operator) {
        case "+":
          setResult((prevResult) => (prevResult !== null ? prevResult + inputValue : inputValue));
          break;
        case "-":
          setResult((prevResult) => (prevResult !== null ? prevResult - inputValue : -inputValue));
          break;
        case "x":
          setResult((prevResult) => (prevResult !== null ? prevResult * inputValue : inputValue));
          break;
        case "รท":
          setResult((prevResult) => (prevResult !== null ? prevResult / inputValue : inputValue));
          break;
        default:
          setResult(inputValue);
      }
    }
    inputRef.current.value = "";
  }

  function handleOperation(e) {
    e.preventDefault();
    setOperator(e.target.textContent);
    calculate();
  }

  function handleEquals() {
    calculate();
    setOperator("");
  }

  function resetInput(e) {
    e.preventDefault();
    inputRef.current.value = "";
  }

  function resetResult(e) {
    e.preventDefault();
    setResult(null);
    inputRef.current.value = "";
    setOperator("");
  }

  return (
    <div className="calculator">
      <div>
        <h1>Simplest Working Calculator</h1>
      </div>
      <form>
        <p className="display" ref={resultRef}>
          {result !== null ? result : 0}
        </p>
        <input
          pattern="[0-9]"
          ref={inputRef}
          type="number"
          placeholder="Type a number"
        />
        <div className="buttons">
          <button onClick={handleOperation}>+</button>
          <button onClick={handleOperation} className="operator">-</button>
          <button onClick={handleOperation} className="operator">x</button>
          <button onClick={handleOperation} className="operator">รท</button>
          <button onClick={resetInput}>C</button>
          <button onClick={resetResult} className="equal">AC</button>
          <button onClick={handleEquals} className="equal">=</button>
        </div>
      </form>
    </div>
  );
}

export default App;
